import { arrayEquals } from './array';
describe('array', function () {
  describe('arrayEquals', function () {
    it('should match two arrays based on values, ignoring order', function () {
      var array1 = ['jeff', 'fred', 'cecily'];
      var array2 = ['fred', 'cecily', 'jeff'];
      var result = arrayEquals(array1, array2);
      expect(result).toBeTruthy();
    });
    it('should not mutate the original arrays', function () {
      var array1 = ['jeff', 'fred', 'cecily'];
      var array2 = ['fred', 'cecily', 'jeff'];
      var array1String = array1.toString();
      var array2String = array2.toString();
      arrayEquals(array1, array2);
      expect(array1.toString()).toBe(array1String);
      expect(array2.toString()).toBe(array2String);
    });
  });
});